import Link from "next/link";
import { useResponsive } from "../hooks/useResponsive";
import styled from "@emotion/core";

const Header = styled.div``;

export const Navbar = () => {
  const { isDesktop } = useResponsive();

  return (
    <div className="header">
      <div className="sub-header">
        <Link className="sub-header-text" href="/login">
          ลงทะเบียน
        </Link>
        <img
          className="separate"
          alt="separate-icon"
          src={require("../../assets/separate-icon.png")}
        />
        <Link className="sub-header-text" href="/register">
          เข้าสู่ระบบ
        </Link>
      </div>
      <div className="main-header">
        {isDesktop ? (
          <img
            className="logo"
            alt="gypstore-logo"
            src={require("../../assets/logo.png")}
          />
        ) : (
          <img
            className="logo"
            alt="gypstore-logo"
            src={require("../../assets/logo-without-text.png")}
          />
        )}
        <input className="search" type="search" placeholder="ค้นหาสินค้า" />

        <div className="text-options">
          <Link className="text-option" href="/shop">
            สินค้า
          </Link>
          <Link className="text-option" href="/service">
            บริการ
          </Link>
          <Link className="text-option" href="/rewards">
            คะแนนสะสม
          </Link>
        </div>

        <div className="icon-options">
          <img
            className="icon-option"
            alt="quotation-icon"
            src={require("../../assets/quotation-icon.png")}
            onMouseOver={e =>
              (e.currentTarget.src = require("../../assets/quotation-icon-orange.png"))
            }
            onMouseOut={e =>
              (e.currentTarget.src = require("../../assets/quotation-icon.png"))
            }
          />
          <img
            className="icon-option"
            alt="cart-icon"
            src={require("../../assets/cart-icon.png")}
            onMouseOver={e =>
              (e.currentTarget.src = require("../../assets/cart-icon-orange.png"))
            }
            onMouseOut={e =>
              (e.currentTarget.src = require("../../assets/cart-icon.png"))
            }
          />
        </div>
      </div>
    </div>
  );
};
