import styled from "@emotion/styled";
import { Icon } from "antd";

const FooterContainer = styled.div`
  display: flex;
  flex-direction: column;
  background: black;
  padding: 1rem 7rem 0rem 7rem;
`;

const MainFooter = styled.div`
  bottom: 0;

  height: 21rem;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;

  & h2,
  h3,
  h4 {
    color: white;
  }
  h5 {
    font-size: 16px;
  }
  color: white !important;

  & > div {
    padding-top: 1rem;
    display: flex;
    flex-direction: column;
  }
`;

const SubFooter = styled.div`
  display: flex;
  justify-content: space-between;
  border-top: .05rem solid white;
  p {
    padding-top: .5rem;
    color: white;
    font-size: 10px;
  }
`;

const ContactUs = styled.div`
  & h2,
  h3,
  h4 {
    margin-bottom: 0rem;
  }
  p {
    margin-bottom: 0.5rem;
    padding-left: 1.75rem;
  }
`;

const Service = styled.div``;
const Payment = styled.div``;

const ContactLogoList = styled.div`
  width: 8rem;
  display: flex;
  justify-content: space-between;
  i {
    font-size: 1.75em;
  }
`;

const LineQR = styled.img`
  margin-top: 1rem;
  width: 8rem;
`;

const PaymentMethod = styled.img`
  width: 15rem;
`;

export const Footer = () => {
  return (
    <FooterContainer>
      <MainFooter>
        <ContactUs>
          <h3>ติดต่อเรา</h3>
          <ContactLogoList>
            <Icon type="phone" theme="filled" />
            <Icon type="facebook" theme="filled" />
            <Icon type="instagram" theme="filled" />
          </ContactLogoList>
          <LineQR src="/assets/line-qr.png" alt="line-qr" />
          <p>@gypstore</p>

          <h5>
            จันทร์ - ศุกร์ 8.00-17.00 น.
            <br />
            เสาร์ 08.00-12.00 น.
          </h5>
        </ContactUs>
        <Service>
          <h3>บริการ</h3>
          <h5>ติดต่อเรา</h5>

          <h5>วิธีการสั่งซื้อสินค้า</h5>

          <h5>วิธีการชำระเงิน</h5>

          <h5>การจัดส่งสินค้า</h5>

          <h5>ตรวจสอบสถานะการจัดส่ง</h5>
        </Service>
        <Payment>
          <h3>การชำระเงิน</h3>
          <PaymentMethod src="/assets/payment-option.png" alt="payment" />
          <br />
          <h3>ทะเบียนพาณิชย์อิเล็กทรอนิกส์</h3>
        </Payment>
      </MainFooter>
      <SubFooter>
        <p>Copyright @ 2020  Gypstore Co., Ltd.  All Right Reserved.</p>
        <p>บริษัท ยิปสโตร์ จำกัด เลขที่ 14/12 ตำบลบึงคำพร้อย อำเภอลำลูกกา ปทุมธานี 12150</p>
      </SubFooter>
    </FooterContainer>
  );
};
