import styled from "@emotion/styled";

const Container = styled.div`
  height: 30rem;
  width: 100%;
  padding: 3rem;
  margin-bottom: 2rem;
`;

const ProductContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-areas:
    "bg bg . ."
    "bg bg . .";
  height: 100%;
`;

const ProductCardWide = styled.div`
  background-color: black;
  border: 1px solid white;
  width: 100%;
  height: 100%;
  grid-area: bg;
`;

const ProductCardWideSM = styled.div`
  background-color: black;
  border: 1px solid white;
  width: 100%;
  height: 100%;
`;

type Props = {
  title: String;
};

export const ProductSection = (props: Props) => {
  const { title = "ฝ้าเพดานหินอ่อน" } = props;
  return (
    <Container>
      <h2>{title}</h2>
      <ProductContainer>
        <ProductCardWide />
        <ProductCardWideSM />
        <ProductCardWideSM />
        <ProductCardWideSM />
        <ProductCardWideSM />
      </ProductContainer>
    </Container>
  );
};
