import styled from '@emotion/styled'

const Container = styled.div`
  display: flex;
`

type Props = {
  onChange: (e) => void
  amount: number
}

export const ProductAmountInput = (props: Props) => {
  const { onChange, amount } = props
  return (
    <Container>
      <div onClick={() => onChange(amount - 1)}>-</div>
      <div>{amount}</div>
      <div onClick={() => onChange(amount + 1)}>+</div>
    </Container>
  )
}
