import { useQuery } from '@apollo/react-hooks'
import { GET_PRODUCT } from './gql'
import styled from '@emotion/styled'
import { Card } from 'antd'
import _ from 'lodash'

const { Meta } = Card

const ProductListContainer = styled.div`
  width: 100%;
  min-height: 100vh;
`

const ProductContainer = styled.div`
  padding: 2rem 4rem;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  width: 100%;
`

const CoverImg = styled.img`
  padding: 2rem;
`
const NoProduct = styled.h2`
  text-align: center;
  background: gray;
  justify-self: center;
`

type Props = {
  category?: string
}

export const ProductList = (props: Props) => {
  const { category = '' } = props
  const { data, loading } = useQuery(GET_PRODUCT, {
    variables: { pName: category },
  })
  if (loading) return <div>'Loading...'</div>
  const productItems = data.productItems as ProductItem[]
  console.log(data)
  return (
    <ProductListContainer>
      <h1>{category}</h1>
      <ProductContainer>
        {_.isEmpty(productItems) && (
          <NoProduct>ไม่มีรายการสินค้าประเภทนี้ในขณะนี้</NoProduct>
        )}
        {productItems.map(p => {
          return (
            <Card
              hoverable
              style={{ width: 250, margin: '0.5rem' }}
              cover={
                <CoverImg alt="example" src="/assets/logo-without-text.png" />
              }
            >
              <Meta title={p.name} description={p.description} />
            </Card>
          )
        })}
      </ProductContainer>
    </ProductListContainer>
  )
}
