import { gql } from 'apollo-boost'

export const GET_PRODUCT = gql`
  query($pName: String) {
    productItems(where: { category: { name: $pName } }) {
      id
      name
      price
      brand
      unitType
      description
      MenuDetail
    }
  }
`

export const GET_PRODUCT_DETAIL = gql`
  query($id: String) {
    product(where: { id: $id }) {
      id
      name
      price
      brand
      unitType
      description
      MenuDetail
      category {
        name
      }
    }
  }
`
