  Select,
  Row,
  Col,
  Checkbox,
  AutoComplete, } from "antd";
import { useState } from "react";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: 380px;
  margin: 30px 0px;
  align-items: center;

`;

