  width: 90%;
  margin: 3rem auto;
  background-color: white;
  padding: 2rem 2rem;
  display: grid;
  grid-gap: 2rem;
  grid-template-columns: 1fr 1.25fr 1fr;
`
export const ProductDetail = () => {
  return (
    <Container>
      <ProductImage />
      <ProductInformation />
      <div>payment</div>
    </Container>
  )
}
