import styled from "@emotion/styled";

const Group = styled.div`
  $sub-color: grey;
  $main-color: black;

  @mixin shrinkLabel {
    top: -14px;
    font-size: 12px;
    color: $main-color;
  }

  position: relative;
  margin: 45px 0;

  input[type='password'] {
    letter-spacing: 0.3em;
  }
`;

const Form = styled.div`
  background: none;
  background-color: white;
  color: $sub-color;
  font-size: 18px;
  padding: 10px 10px 10px 5px;
  display: block;
  width: 100%;
  border: none;
  border-radius: 0;
  border-bottom: 1px solid $sub-color;
  margin: 25px 0;

  &:focus {
    outline: none;
  }

  &:focus ~ .form-input-label {
    @include shrinkLabel();
  }
`;

const FormLabel = styled.div`
  color: $sub-color;
  font-size: 16px;
  font-weight: normal;
  position: absolute;
  pointer-events: none;
  left: 5px;
  top: 10px;
  transition: 300ms ease all;

  &.shrink {
    @include shrinkLabel();
  }
`;


export const FormInput = ({ label, ...otherProps }) => (
  <Group>
    <Form>
      <input className='Form' {...otherProps} />
      {
        label ? (
          <label 
            className={`${
              otherProps.value.length ? 'shrink' : ''
            } form-input-label`}
          >
            {label}
        </label>
        ) : null}
    </Form>
  </Group>
);
