import styled from "@emotion/styled";
import { Icon } from "antd";

const FooterContainer = styled.div`
  bottom: 0;

  height: 30rem;
  background: black;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;

  & h2,
  h3,
  h4 {
    color: white;
  }
  color: white !important;

  & > div {
    padding-left: 7rem;
    padding-top: 2.5rem;
    display: flex;
    flex-direction: column;
  }
`;

const ContactUs = styled.div``;
const Service = styled.div``;
const Payment = styled.div``;

const ContactLogoList = styled.div`
  width: 10rem;
  display: flex;
  justify-content: space-between;
  i {
    font-size: 2em;
  }
`;

const LineQR = styled.img`
  margin-top: 2rem;
  width: 10rem;
`;

const PaymentMethod = styled.img`
  width: 15rem;
`;

export const Footer = () => {
  return (
    <FooterContainer>
      <ContactUs>
        <h2>ติดต่อเรา</h2>
        <ContactLogoList>
          <Icon type="phone" theme="filled" />
          <Icon type="facebook" theme="filled" />
          <Icon type="instagram" theme="filled" />
        </ContactLogoList>
        <LineQR src="/assets/line-qr.png" alt="line-qr" />
        <p>@gypstore</p>

        <h4>
          จันทร์ - ศุกร์ 8.00-17.00 น.
          <br />
          เสาร์ 08.00-12.00 น.
        </h4>
      </ContactUs>
      <Service>
        <h3>บริการ</h3>
        <h5>ติดต่อเรา</h5>

        <h5>วิธีการสั่งซื้อสินค้า</h5>

        <h5>วิธีการชำระเงิน</h5>

        <h5>การจัดส่งสินค้า</h5>

        <h5>ตรวจสอบคะแนนสะสม</h5>
      </Service>
      <Payment>
        <h3>การชำระเงิน</h3>
        <PaymentMethod src="/assets/payment-option.png" alt="payment" />
      </Payment>
    </FooterContainer>
  );
};
