import Link from "next/link";
import { useResponsive } from "../hooks/useResponsive";
import styled from "@emotion/styled";

const Header = styled.div`
  height: 120px;
  width: 100%;
  display: flex;
  flex-direction: column;
  position: -webkit-sticky;
  position: sticky;
  top: 0;
  z-index: 9999;
`;

const SubHeader = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  height: 40px;
  background-color: #ff8e0a;
  padding: 0px 30px;

  a:hover {
    color: #fff;
  }
  img {
    height: 40px;
    width: auto;
    align-items: center;
  }
`;

export const Navbar = () => {
  const { isDesktop } = useResponsive();

  return (
    <Header>
      <SubHeader>
        <Link href="/login">ลงทะเบียน</Link>
        <img
          className="separate"
          alt="separate-icon"
          src="/assets/separate-icon.png"
        />
        <Link href="/register">เข้าสู่ระบบ</Link>
      </SubHeader>
      <div className="main-header">
        {isDesktop ? (
          <img className="logo" alt="gypstore-logo" src="/assets/logo.png" />
        ) : (
          <img
            className="logo"
            alt="gypstore-logo"
            src="/assets/logo-without-text.png"
          />
        )}
        <input className="search" type="search" placeholder="ค้นหาสินค้า" />

        <div className="text-options">
          <Link href="/shop">สินค้า</Link>
          <Link href="/service">บริการ</Link>
          <Link href="/rewards">คะแนนสะสม</Link>
        </div>

        <div className="icon-options">
          <img
            className="icon-option"
            alt="quotation-icon"
            src="../../assets/quotation-icon.png"
          />
          <img
            className="icon-option"
            alt="cart-icon"
            src="../../assets/cart-icon.png"
          />
        </div>
      </div>
    </Header>
  );
};
